mapper.update:
update tb_job set
name=#{name}
,city=#{city} 
,edu=#{edu} 
,experience=#{experience}
,money=#{money}
,intro=#{intro}
,pub_time=#{pub_time} 
where id=#{id}

mapper.select:
select tb_enterprise.name from
tb_enterprise where
username=#{username}

SELECT TOP ${num} * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY tb_job.id) AS RowNumber, tb_job.*,tb_enterprise.id ep_id,tb_enterprise.img ep_img FROM tb_job,tb_enterprise
and tb_job.name like '%${kw}%'
and tb_job.city = #{city}
tb_job.edu = #{edu}
tb_job.money = #{money}
and tb_enterprise.username=tb_job.username
) as A WHERE RowNumber > ${num}*(${page}-1)

select * from
tb_vitae_job where
job_id=#{id} and username=#{username}
